exports.build = function (text) {
    var vsm = [];
    var vsmAsText = text.split("\n"); // each line represents a sentence's entities. If a sentence is represented as #Not_Found, it means that it has no entities
    for (var i = 0; i < vsmAsText.length; i++) {
       var oneSentence = JSON.parse(vsmAsText[i]);
        vsm.push(oneSentence);
    }
    return vsm;
};
