var fs = require('fs');

var _file = fs.readFileSync("0_vsm.txt");
var vsm = [];
    var vsmAsText = _file.toString().split("\n"); // each line represents a sentence's entities. If a sentence is represented as #Not_Found, it means that it has no entities
    for (var i = 0; i < vsmAsText.length; i++) {
       var oneSentence = JSON.parse(vsmAsText[i]);
        vsm.push(oneSentence);
    }

console.log(vsm);
